import * as action from "./acction";

 export const vote = (state=0,action) => {
    switch(action.type){
      case action.UPVOTE:
        return state + 1;
        case action.DOWNVOTE:
          return state - 1;
    }
  }


  export const mems = (state = [], action) => {
    switch (action.type) {
      case 'FETCH_MEMS_SUCCESS':
        return [
          ...action.mems
        ]
      default:
        return state
    }
  }
