import React from "react";
import "./App.css";
import Mem from "./component/Mem";
import Leyout from "./component/Layouts/index";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";

class App extends React.Component {
  state = {
    mems: [],
  };


async componentDidMount() {
  const mems = await (await fetch("http://localhost:3004/mems")).json();
  this.setState({ mems });
}
render() {
  return (
    <div className="App">
      <Router>
        <Leyout>
          <Switch>
            <Route exact path="/">
              <Redirect to="/regular" />
            </Route>
            <Route path="/regular">
            <Paper>
        {this.state.mems.map((mem) => (
          <Mem key={mem.id} mem={mem} />
        ))}
      </Paper>
            </Route>
            <Route path="/hot">
            <Paper>
        {this.state.mems.map((mem) => (
          <Mem key={mem.id} mem={mem} />
        ))}
      </Paper>
            </Route>
            <Route path="*">
              <MemList />
            </Route>
          </Switch>
        </Leyout>
      </Router>
    </div>
  );
}}

export default App;
