 return( props.filterHot(props.mems),
    console.log("return hot", props.hots),
 <MemsPage mems={props.mems} handleUpvoteMem={handleUpvoteMem}  handleDownvoteMem={handleDownvoteMem}/>
    ) 

};

const mapStateToProps = (state) => {
    console.log("mapStateToPorps", state);
  return {
    mems: state.mems,
    filter: state.filter,
  };
};

const mapDispatchToProps = (dispatch) =>
  bindActionCreators(
    {
      memsFetched,
      updateMem,
      filterHot,
      filterRegular,
    },
    dispatch
  );

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(MemsList));
