import React from "react";
import {
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
  Grid,
  IconButton,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import {
  ArrowUpwardRounded,
  ArrowDownwardRounded,
  Star,
} from "@material-ui/icons";

const useStyles = makeStyles((theme) => ({
  memCard: {
    // "margin-left": "20%",

    width: "70%",
    margin: "auto",
    "margin-bottom": "20px",
  },
  memImg:{
    width: "60%", 
    marginTop: "5%",
  },
  memTitle: {
    width: "60%",
    margin: "auto",  },
}));

const Mem = ({ mem, onUpvoteClick, onDownvoteClick, onMarkStar }) => {
  const classes = useStyles();

  return (
    <Card className={classes.memCard}>
      <CardContent className={classes.memTitle}>
        <Grid container justify="center">
          <Grid item sm={3}>
            <IconButton
              onClick={() => onMarkStar(mem)}
              color={mem.star ? "primary" : "default"}
            >
              <Star fontSize="large"/>
            </IconButton>
          </Grid>
          <Grid item sm={9}>
            <Typography variant="h4" align="left">{mem.title}</Typography>

          </Grid>
        </Grid>
      </CardContent>
      <CardContent>
        <img
              src={mem.img}
              alt={mem.title}
              className={classes.memImg}
            />
      </CardContent>
      <CardActions className={classes.memAction}>
        <Grid container>
          <Grid item sm>
            <Button
              name="upvote"
              variant="contained"
              style={{
                width: "80%",
              }}
              onClick={() => onUpvoteClick(mem)}
            >
              <ArrowUpwardRounded />
              {mem.upvotes}
            </Button>
          </Grid>
          <Grid item sm>
            <Button
              variant="contained"
              color="primary"
              style={{
                width: "80%",
              }}
              onClick={() => onDownvoteClick(mem)}
            >
              <ArrowDownwardRounded />
              {mem.downvotes}
            </Button>
          </Grid>
        </Grid>
      </CardActions>
    </Card>
  );
};

export default Mem;
