import * as React from "react";
import "./App.css";
import Mem from "./component/Mem";
import Leyout from "./component/Layouts/index";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import Paper from "@material-ui/core/Paper";
import { memsFetched } from "./redux/actions/actions";
import { connect } from "react-redux";

const styles = (theme) => ({
  Paper: { padding: 20, marginBottom: 10 },
});

class App extends React.Component {
  async componentDidMount() {
    const mems= this.props;
    this.props.memsFetched(mems);
  }

  handleUpvoteMem = async mem => {
    try {
      await this.props.actions.up(course);
    } catch (error) {
      toast.error("Delete failed. " + error.message, { autoClose: false });
    }
  };

  render() {
    return (
      <div className="App">
        <Router>
          <Leyout>
            <Switch>
              <Route exact path="/">
                <Redirect to="/regular" />
              </Route>
              <Route path="/regular">
                <Paper
                  styles={{ padding: 20, marginTop: 10, marginBottom: 10 }}
                >
                  {this.props.mems
                    .filter((mem) => mem.upvotes + mem.downvotes < 6)
                    .map((mem) => (
                      <Mem key={mem.id} mem={mem} onUpvoteClick={this.handleUpvoteMem}/>
                    ))}
                </Paper>
              </Route>
              <Route path="/hot">
                <Paper styles={{ padding: 20, marginBottom: 10 }}>
                  {this.props.mems
                    .filter((mem) => mem.upvotes + mem.downvotes > 5)
                    .map((mem) => (
                      <Mem key={mem.id} mem={mem} />
                    ))}
                </Paper>
              </Route>
              <Route path="*">
                <Paper>
                  {this.props.mems.map((mem) => (
                    <Mem key={mem.id} mem={mem} />
                  ))}
                </Paper>
              </Route>
            </Switch>
          </Leyout>
        </Router>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    mems: state.mems,
  };
};
const mapDispatchToProps = { memsFetched };

export const AppContainer = connect(mapStateToProps, mapDispatchToProps)(App);
