import * as action from "./actionsType";
import * as memsApi from "../../api/memsApi";

export function updateMemSuccess(mem) {
  return {
    type: action.UPDATE_MEM,
    mem
  };
}

export function memsFetchedSuccess(mems) {
  return { type: action.FETCH_MEMS_SUCCESS, mems };
}

export function memsFetched(){
  return function(dispatch) {
    console.log("tu probuje");
    return memsApi.getMems().then(mems => {
      dispatch(memsFetchedSuccess(mems));
    }).catch(error => {
      throw error;
    })
  }
}

export function updateMem(mem){
  return function(dispatch) {
    return memsApi.updateMem(mem).then(changeMem => {
      dispatch(updateMem(changeMem));
    }).catch(error => {
      throw error;
    })
  }
}
