import React  from "react";
import { connect } from "react-redux";
import Mem from "./Mem";
import { withRouter } from "react-router-dom";
import { memsFetched, updateMem } from "../redux/actions/actions";
import Leyout from "./Layouts/index";
import Paper from "@material-ui/core/Paper";



class MemsList extends React.Component {
    async componentDidMount() {
        const mems = this.props;
        this.props.memsFetched(mems);
      }

    handleUpvoteMem = async (mem) => {
        let newMem = { ...mem };
        newMem.upvotes = newMem.upvotes + 1;
        await this.props.updateMem(newMem);
      };
    
     handleDownvoteMem = async (mem) => {
        let newMem = { ...mem };
        newMem.downvotes = newMem.downvotes - 1;
        await this.props.updateMem(newMem);
      };
      render() {
    return (
        <Leyout>
        <Paper
          styles={{ padding: 20, marginTop: 10, marginBottom: 10 }}
        >
          {this.props.mems.map((mem) => (
            <Mem
              key={mem.id}
              mem={mem}
              onUpvoteClick={handleUpvoteMem}
              onDownvoteClick={handleDownvoteMem}
            />
          ))}
        </Paper>
      </Leyout>
    );
  }}
  
  const mapStateToProps = (state) => {
    return {
      mems: state.mems,
    };
  };

  const mapDispatchToProps =(dispatch )=>  bindActionCreators(
    { memsFetched, updateMem },dispatch);
  
  
  export default connect(mapStateToProps, mapDispatchToProps)(withRouter(MemsList));
