import React, {  useEffect } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import { memsFetched, updateMem, filterHot, filterRegular } from "../redux/actions/memActions";
import { bindActionCreators } from "redux";
import { useLocation } from 'react-router-dom';
import MemsPage from "./MemsPage";

const MemsList = (props) => {

  useEffect(() => {
    const fetched = props.memsFetched();
  }, []);

  const handleUpvoteMem = async (mem) => {
    let newMem = { ...mem };
    newMem.upvotes = newMem.upvotes + 1;
    await props.updateMem(newMem);
  };

  const handleDownvoteMem = async (mem) => {
    let newMem = { ...mem };
    newMem.downvotes = newMem.downvotes - 1;
    await props.updateMem(newMem);
  };

  if(useLocation().pathname === "/hot"){
      return(
          console.log(props.mems),
        props.filterHot(props.mems),
        props.filterRegular(props.mems),
      <MemsPage mems={props.hots} handleUpvoteMem={handleUpvoteMem}  handleDownvoteMem={handleDownvoteMem}/>
      )}
 return( <MemsPage mems={props.mems} handleUpvoteMem={handleUpvoteMem}  handleDownvoteMem={handleDownvoteMem}/>
    ) 

};

const mapStateToProps = (state) => {
  return {
    mems: state.mems,
    hots: state.hots,
    regulars: state.regulars
  };
};

const mapDispatchToProps = (dispatch) =>
  bindActionCreators(
    {
      memsFetched,
      updateMem,
      filterHot,
      filterRegular,
    },
    dispatch
  );

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withRouter(MemsList));
