import React from "react";
import {
  Card,
  CardHeader,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  Button,
  Grid
} from "@material-ui/core";
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  memCard: {

  }
})
);

const Mem = ({mem}) => {
  const classes = useStyles();
  return (
    <div>
      <Grid
  container
  spacing={0}
  direction="column"
  alignItems="center"
  justify="center"
  style={{ minHeight: '100vh' }}
 >
      <Card className={classes.memCard}>
      <CardHeader title=  {mem.title}/>
        <CardMedia
          style={{
            height: 0,
            paddingTop: "26.25%", // 16:9,
            marginTop: "30",
          }}
          image={mem.img}
          title={mem.title}
          className="mem-img"
        />
        <CardActions>
          <Button size="small" color="primary">
            Upvote
          </Button>
          <p>{mem.upvotes}</p>
          <Button size="small" color="primary">
            Unvote
          </Button>
          <p>{mem.downvotes}</p>
        </CardActions>
      </Card></Grid>
    </div>
  );
};

export default Mem;
