import * as action from "./actionsType";
import * as memsApi from "../../api/memsApi";

export function upvote(mem) {
  return {
    type: action.UPVOTE,
    memsFetchedSuccess
  };
}

export function downvote(mem) {
  return {
    type: action.DOWNVOTE,
    mem
  };
}

export function memsFetchedSuccess(mems) {
  return { type: action.FETCH_MEMS_SUCCESS, mems };
}

export function memsFetched(){
  return function(dispatch) {
    console.log("tu probuje");
    return memsApi.getMems().then(mems => {
      dispatch(memsFetchedSuccess(mems));
    }).catch(error => {
      throw error;
    })
  }
}

export function memUpvote(mem){
  return function(dispatch) {
    return memsApi.updateMem(mem).then(mems => {
      dispatch(upvote(mems));
    }).catch(error => {
      throw error;
    })
  }
}
