import React from "react";
import {
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
  Grid,
  IconButton,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import {
  ArrowUpwardRounded,
  ArrowDownwardRounded,
  Star,
} from "@material-ui/icons";
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';

const useStyles = makeStyles((theme) => ({
  memCard: {
    // "margin-left": "20%",

    width: "70%",
    margin: "auto",
    "margin-bottom": "20px",
  },
  memTitle: {
    margin: "5%",
  },
}));

const Mem = ({ mem, onUpvoteClick, onDownvoteClick, onMarkStar }) => {
  const classes = useStyles();

  return (
    <Card className={classes.memCard}>
      <CardContent className={classes.memTitle}>
        <Typography variant="h5">{mem.title}</Typography>
        <GridListTile key={tile.img} cols={tile.featured ? 2 : 1} rows={tile.featured ? 2 : 1}>
        <img
          src={mem.img}
          alt={mem.title}
          style={{ width: "80%", marginTop: "5%" }}
        />
            <GridListTileBar
              title={tile.title}
              titlePosition="top"
              actionIcon={
                <IconButton
              onClick={() => onMarkStar(mem)}
              color={mem.star ? "primary" : "default"}
            >
              <Star />
            </IconButton>
              }
              actionPosition="left"
              className={classes.titleBar}
            />
            </GridListTile>
      </CardContent>
      <CardActions className={classes.memAction}>
        <Grid container>
          <Grid item sm>
            <Button
              name="upvote"
              variant="contained"
              style={{
                width: "80%",
              }}
              onClick={() => onUpvoteClick(mem)}
            >
              <ArrowUpwardRounded />
              {mem.upvotes}
            </Button>
          </Grid>
          <Grid item sm>
            <Button
              variant="contained"
              color="primary"
              startIcon={<ArrowDownwardRounded />}
              style={{
                width: "80%",
              }}
              onClick={() => onDownvoteClick(mem)}
            >
              <ArrowDownwardRounded />
              {mem.downvotes}
            </Button>
          </Grid>
        </Grid>
      </CardActions>
    </Card>
  );
};

export default Mem;
