import {
  FETCH_MEMS_SUCCESS,
  UPDATE_MEM,
  UPVOTE,
  DOWNVOTE,
} from "../actions/actionsType";

const mems = (state = [], action) => {
  switch (action.type) {
    case FETCH_MEMS_SUCCESS:
      return [...action.mems];
    case UPDATE_MEM:
      return state.map((mem) => (mem.id === action.mem.id ? action.mem : mem));
    default:
      return state;
  }
};

export default mems;
