import {
    HOT,
    REGULAR
  } from "../actions/actionsType";
  
  const INIT_STATE = {
    hots: [],
    regulars: [],
  };
  
  const filter = (state = INIT_STATE, action) => {
    switch (action.type) {
      case REGULAR:
        return {...state, regulars: action.payload.filter((mem)=> mem.upvotes + mem.downvotes < 6) };
      case HOT:
          console.log("reducer",action.payload);
        return {...state, hots: action.payload.filter((mem)=> mem.upvotes + mem.downvotes > 5) };
      default:
        return state;
    }
  };
  
  export default filter;
