import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import Mem from "./Mem";
import { withRouter } from "react-router-dom";
import { memsFetched, updateMem } from "./redux/actions/actions";
import Leyout from "./Layouts/index";
import { bindActionCreators } from "redux";

const MemsList = (props) => {
    const [mems, setMems] = useState([]);
  
    useEffect(()=>{
        const mems = this.props.memsFetched(props.mems);
        setMems(mems);
    });

    const handleUpvoteMem = async (mem) => {
        let newMem = { ...mem };
        newMem.upvotes = newMem.upvotes + 1;
        await props.updateMem(newMem);
      };
    
      const handleDownvoteMem = async (mem) => {
        let newMem = { ...mem };
        newMem.downvotes = newMem.downvotes - 1;
        await props.updateMem(newMem);
      };
  
    return (
        <Leyout>
        <Paper
          styles={{ padding: 20, marginTop: 10, marginBottom: 10 }}
        >
          {this.props.mems.map((mem) => (
            <Mem
              key={mem.id}
              mem={mem}
              onUpvoteClick={this.handleUpvoteMem}
              onDownvoteClick={this.handleDownvoteMem}
            />
          ))}
        </Paper>
      </Leyout>
    );
  };
  
  const mapStateToProps = (state) => {
    return {
      mems: state.mems,
    };
  };
  const mapDispatchToProps = { memsFetched, updateMem };
  
  
  export default connect(mapStateToProps, mapDispatchToProps)(withRouter(MemsList));
