import React from "react";
import "./App.css";
import Mem from "./component/Mem";
import Leyout from "./component/Layouts/index";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import Paper from "@material-ui/core/Paper";
import { withStyles } from "@material-ui/core/styles";
import store from './component/MemStore';
import {memsFetched}  from "./reducer/action";
import { connect } from "react-redux";



const styles = (theme) => ({
  Paper: { padding: 20, marginTop: 10, marginBottom: 10 },
});




class App extends React.Component {
  
async componentDidMount() {
  const mems = await (await fetch("http://localhost:3004/mems")).json();
  this.setState({ mems });
}
render() {
  return (
    <div className="App">
      <Router>
        <Leyout>
          <Switch>
            <Route exact path="/">
              <Redirect to="/regular" />
            </Route>
            <Route path="/regular">
            <Paper>
        {this.state.mems.map((mem) => (
          <Mem key={mem.id} mem={mem} />
        ))}
      </Paper>
            </Route>
            <Route path="/hot">
            <Paper>
        {this.state.mems.map((mem) => (
          <Mem key={mem.id} mem={mem} />
        ))}
      </Paper>
            </Route>
            <Route path="*">
            <Paper>
        {this.state.mems.map((mem) => (
          <Mem key={mem.id} mem={mem} />
        ))}
      </Paper>
            </Route>
          </Switch>
        </Leyout>
      </Router>
    </div>
  );
}}

const mapStateToProps = (state) => {
  return {
    mems: state.mems
  }
};
const mapDispatchToProps = { memsFetched };

export const AppContainer = connect(mapStateToProps, mapDispatchToProps)(App);
