import React from "react";
import {
  Card,
  CardHeader,
  CardMedia,
  CardContent,
  Typography,
  CardActions,
  Button,
  Grid,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  memCard: {
    height: 1000,
    "margin-left": "10%",
    width: "80%",
  },
  memCard: {
    margin: "120px auto 50px",
    maxWidth: 345,
    overflow: "visible"
  },
  media: {
    margin: "-70px auto 0",
    width: "80%",
    height: 140,
    borderRadius: "4px",
    boxShadow: "0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23)",
    position: "relative",
    zIndex: 1000
  }
}));

const Mem = ({ mem }) => {
  const classes = useStyles();
  return (
    <div>
      <Card className={classes.memCard}>
        <CardHeader title={mem.title} />
        <CardMedia
        className={classes.media}
          // style={{
          //   height: 0,
          //   paddingTop: "56.25%",
          // }}
          image={mem.img}
          title={mem.title}
          className="mem-img"
        />
        <CardActions>
          <Button size="small" color="primary">
            Upvote
          </Button>
          <p>{mem.upvotes}</p>
          <Button size="small" color="primary">
            Unvote
          </Button>
          <p>{mem.downvotes}</p>
        </CardActions>
      </Card>
    </div>
  );
};

export default Mem;
