import * as React from "react";
import "./App.css";
import Mem from "./component/Mem";
import Leyout from "./component/Layouts/index";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";
import Paper from "@material-ui/core/Paper";
import { withStyles } from "@material-ui/core/styles";
import store from "./component/MemStore";
import { memsFetched } from "./reducer/actions";
import { connect } from "react-redux";

const styles = (theme) => ({
  Paper: { padding: 20, marginTop: 10, marginBottom: 10 },
});

class App extends React.Component {
  // async 
  componentDidMount() {
    // const mems = await (await fetch("http://localhost:3004/mems")).json();
    // this.props.memsFetched({ mems });
    fetch("http://localhost:3004/mems")
      .then(res => res.json())
      .then(json => this.props.memsFetched(json.results));
  }


  render() {
    return (
      <div className="App">
        <Router>
          <Leyout>
            <Switch>
              <Route exact path="/">
                <Redirect to="/regular" />
              </Route>
              <Route path="/regular">
                <Paper>
                  {this.props.mems.map((mem) => (
                    <Mem key={mem.id} mem={mem} />
                  ))}
                </Paper>
              </Route>
              <Route path="/hot">
                <Paper>
                  {this.props.mems.map((mem) => (
                    <Mem key={mem.id} mem={mem} />
                  ))}
                </Paper>
              </Route>
              <Route path="*">
                <Paper>
                  {this.props.mems.map((mem) => (
                    <Mem key={mem.id} mem={mem} />
                  ))}
                </Paper>
              </Route>
            </Switch>
          </Leyout>
        </Router>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    mems: state.mems
  };
};
const mapDispatchToProps = { memsFetched };

export const AppContainer = connect(mapStateToProps, mapDispatchToProps)(App);
